import React,{useState} from 'react';
import axios from 'axios';
const Studentupdate = () => {
  const [query, setQuery] = useState("");
  const [results, setResults] = useState([]);

  const handleChange = (e) => {
    setQuery(e.target.value);
  };

  const   handleSearch = async () => {
    try {
        const response = await axios.get(`http://localhost:5000/api/searchstudent`, {
          params: { name: query.toLowerCase() }, 
        });
        setResults(response.data);
      } catch (error) {
        console.error("Error fetching search results:", error);
      }
  };


return (
    <div className="flex justify-center items-center min-h-screen bg-gray-900">
    <div className="flex items-center border border-gray-300 rounded-md p-2 w-full max-w-md mx-auto">
      <input
        type="text"
        value={query}
        onChange={handleChange}
        placeholder="Search by name"
        className="flex-grow p-2 outline-none text-gray-700 bg-transparent"
      />
     
      <button
        onClick={handleSearch}
        className="ml-2 p-2 bg-blue-500 text-white rounded-md hover:bg-blue-600"
      >
        Search
      </button>
    </div>

    {results.length > 0 && (
        <table className="table-auto w-full mt-4 border-collapse border border-gray-300">
          <thead>
            <tr className="bg-gray-200">
              <th className="p-2 border border-gray-300">Name</th>
              <th className="p-2 border border-gray-300">Roll Number</th>
              <th className="p-2 border border-gray-300">Phone Number</th>
             
            </tr>
          </thead>
          <tbody>
            {results.map((item) => (
              <tr key={item.id} className="odd:bg-white even:bg-gray-100">
                <td className="p-2 border border-gray-300">{item.name}</td>
                <td className="p-2 border border-gray-300">{item.roll_no}</td>
                <td className="p-2 border border-gray-300">{item.contact_no}</td>
               
              </tr>
            ))}
          </tbody>
        </table>
      )}

      
      {results.length === 0 && query && (
        <p className="text-gray-500 mt-4">No results found.</p>
      )}

    </div>
  );
}

export default Studentupdate;
