import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Addteacher() {
  const [subjects, setSubjects] = useState([]);
  const [formData, setFormData] = useState({
    name: '',
    age: '',
    address: '',
    phone_number: '',
    subject_id: '',
    experience: '',
    password: '',
  });

  // Fetch subjects for dropdown
  useEffect(() => {
    axios.get('/api/subjects')
      .then((response) => setSubjects(response.data))
      .catch((error) => console.error('Error fetching subjects:', error));
  }, []);

  // Handle form input change
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

 
  const handleSubmit = (e) => {
    e.preventDefault();
    axios.post('/api/teachers', formData)
      .then((response) => {
        console.log(response.data.message);
        // Reset form or show success message
      })
      .catch((error) => console.error('Error registering teacher:', error));
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>Name:</label>
      <input type="text" name="name" onChange={handleChange} required />

      <label>Age:</label>
      <input type="number" name="age" onChange={handleChange} required />

      <label>Address:</label>
      <input type="text" name="address" onChange={handleChange} required />

      <label>Phone Number:</label>
      <input type="text" name="phone_number" onChange={handleChange} required />

      <label>Subject:</label>
      <select name="subject_id" onChange={handleChange} required>
        <option value="">Select a subject</option>
        {subjects.map((subject) => (
          <option key={subject.id} value={subject.id}>{subject.name}</option>
        ))}
      </select>

      <label>Experience:</label>
      <input type="text" name="experience" onChange={handleChange} required />

      <label>Password:</label>
      <input type="password" name="password" onChange={handleChange} required />

      <button type="submit">Register</button>
    </form>
  );
}

export default Addteacher;
