import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const Teachers = () => {
  const { id } = useParams();
  const [teacher, setTeacher] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchteacherDetails = async () => {
      try {
        const response = await axios.get(`/api/teachers/${id}`);
        if (response.data.success) {
          setTeacher(response.data.teacher);
          console.log(teacher);
        } else {
          setError("Failed to fetch teacher details.");
        }
      } catch (err) {
        setError("Error fetching teacher details.");
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchteacherDetails();
  }, [id]);

  if (loading) return <p className="text-center text-white">Loading...</p>;
  if (error) return <p className="text-center text-red-500">{error}</p>;

  return (
    <div className="flex justify-center items-center min-h-screen bg-grey-900">
      <div className="w-full max-w-md p-8 space-y-6 bg-gray-800 rounded-lg shadow-lg">
        <h2 className="py-2 px-4 font-poppins font-medium text-xl text-white bg-grey-500 rounded-md">
          Teacher Details
        </h2>
        <div className="text-white space-y-4">
          <p><strong>Name:</strong> {teacher.name}</p>
          <p><strong>Age:</strong> {teacher.age}</p>
          <p><strong>Class:</strong> {teacher.address}</p>
          <p><strong>Subject:</strong> {teacher.name}</p>
          <p><strong>Phone Number:</strong> {teacher.phone_number}</p>
          <p><strong>Experience:</strong> {teacher.experience}</p>
        </div>
      </div>
    </div>
  );
};

export default Teachers;
