import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

const Updatedetailstea = () => {
  const { id } = useParams();
  const navigate = useNavigate();

  const [teacher, setTeacher] = useState({
    teacher_name: '',
    age: '',
    address: '',
    phone_number: '',
    experience: '',
    classes: [], 
  });
  const [allClasses, setAllClasses] = useState([]); 
  const [newClassId, setNewClassId] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchTeacherDetails = async () => {
      try {
        const response = await axios.get(`/api/teachers/${id}`);
        if (response.data.success) {
          console.log("Teacher data:", response.data.teacher);  // Debugging line
          console.log("Classes assigned to teacher:", response.data.class);  // Debugging line
  
          setTeacher((prevTeacher) => ({
            ...response.data.teacher,
            classes: Array.isArray(response.data.class) ? response.data.class : [response.data.class], // Wrap in array if not already an array
          }));
        } else {
          setError("Failed to fetch teacher details.");
        }
        setLoading(false);
      } catch (err) {
        setError("Error fetching teacher details.");
        console.error(err);
      }
    };
  
    const fetchAllClasses = async () => {
      try {
        const response = await axios.get(`/api/classes`);
        console.log("All available classes:", response.data);  // Debugging line
  
        // Ensure we access `response.data.classes` if that’s where the array is nested
        setAllClasses(Array.isArray(response.data.classes) ? response.data.classes : []);
      } catch (err) {
        console.error("Error fetching all classes", err);
      }
    };
  
    fetchTeacherDetails();
    fetchAllClasses();
  }, [id]);
  
  
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setTeacher((prevTeacher) => ({
      ...prevTeacher,
      [name]: value,
    }));
  };

  
  const handleAddClass = () => {
    if (newClassId && !teacher.classes.some((cls) => cls.id === newClassId)) {
      const selectedClass = allClasses.find((cls) => cls.id === newClassId);
      if (selectedClass) {
        setTeacher((prevTeacher) => ({
          ...prevTeacher,
          classes: [...prevTeacher.classes, selectedClass],
        }));
      }
      setNewClassId('');
    }
  };

  // Remove a class from teacher's classes
  const handleRemoveClass = (classId) => {
    setTeacher((prevTeacher) => ({
      ...prevTeacher,
      classes: prevTeacher.classes.filter((cls) => cls.id !== classId),
    }));
  };

  
  const handleSaveChanges = async () => {
    try {
      const response = await axios.put(`/api/teachers/${id}`, {
        name: teacher.teacher_name,
        age: teacher.age,
        address: teacher.address,
        phone_number: teacher.phone_number,
        experience: teacher.experience,
        classes: teacher.classes.map((cls) => cls.id),
      });
      if (response.data.success) {
        alert("Teacher details updated successfully!");
        navigate(`/teachers/${id}`);
      } else {
        alert("Failed to update teacher details.");
      }
    } catch (err) {
      console.error("Error updating teacher details:", err);
      alert("An error occurred while updating.");
    }
  };

  if (loading) return <p className="text-center text-white">Loading...</p>;
  if (error) return <p className="text-center text-red-500">{error}</p>;

  return (
    <div className="flex flex-col items-center min-h-screen bg-gray-900 p-6">
      <div className="w-full max-w-lg p-8 bg-gray-800 rounded-lg shadow-lg space-y-6">
        <h2 className="text-2xl font-semibold text-white">Edit Teacher Details</h2>
        
        <label className="block text-white">
          <strong>Name:</strong>
          <input
            type="text"
            name="name"
            value={teacher.teacher_name}
            onChange={handleInputChange}
            className="w-full p-2 mt-1 text-black rounded"
          />
        </label>

        <label className="block text-white">
          <strong>Age:</strong>
          <input
            type="number"
            name="age"
            value={teacher.age}
            onChange={handleInputChange}
            className="w-full p-2 mt-1 text-black rounded"
          />
        </label>

        <label className="block text-white">
          <strong>Address:</strong>
          <input
            type="text"
            name="address"
            value={teacher.address}
            onChange={handleInputChange}
            className="w-full p-2 mt-1 text-black rounded"
          />
        </label>

        <label className="block text-white">
          <strong>Phone Number:</strong>
          <input
            type="text"
            name="phone_number"
            value={teacher.phone_number}
            onChange={handleInputChange}
            className="w-full p-2 mt-1 text-black rounded"
          />
        </label>

        <label className="block text-white">
          <strong>Experience:</strong>
          <input
            type="number"
            name="experience"
            value={teacher.experience}
            onChange={handleInputChange}
            className="w-full p-2 mt-1 text-black rounded"
          />
        </label>

        <h3 className="text-lg text-white mt-4">Assigned Classes:</h3>
        <ul className="text-white space-y-2">
          {Array.isArray(teacher.classes) &&
            teacher.classes.map((cls) => (
              <li key={cls.id} className="flex justify-between">
                <span>{cls.class_no}</span>
                <span>{cls.sec}</span>
                <button
                  onClick={() => handleRemoveClass(cls.id)}
                  className="text-red-500 hover:underline"
                >
                  Remove
                </button>
              </li>
            ))}
        </ul>

        <label className="block text-white mt-4">
          <strong>Add New Class:</strong>
          <select
            value={newClassId}
            onChange={(e) => setNewClassId(e.target.value)}
            className="w-full p-2 mt-1 text-black rounded"
          >
            <option value="">Select a class</option>
            {Array.isArray(allClasses) &&
              allClasses.map((cls) => (
                <option key={cls.id} value={cls.id}>
                  {cls.name}
                </option>
              ))}
          </select>
          <button
            onClick={handleAddClass}
            className="mt-2 p-2 bg-blue-500 text-white rounded hover:bg-blue-600"
          >
            Add Class
          </button>
        </label>

        <button
          onClick={handleSaveChanges}
          className="w-full p-2 mt-4 bg-green-500 text-white rounded hover:bg-green-600"
        >
          Save Changes
        </button>
      </div>
    </div>
  );
};

export default Updatedetailstea;
